INSERT INTO public.questions (id, type, topic, difficulty, question, answer, options, explanation, time_limit, min_rating, is_starter, event_context, fun_fact)
VALUES
('ST_MM_001', 'money_math', 'mental_math', 0, 'You buy a coffee for ₹100 and pay with ₹500. Change = ?', '400', NULL, '₹500 − ₹100 = ₹400. Always calculate your change before leaving the counter!', 10, 0, TRUE, NULL, NULL),
('ST_MM_002', 'money_math', 'mental_math', 0, 'You spend ₹200 on Monday and ₹300 on Tuesday. Total spend = ?', '500', NULL, '₹200 + ₹300 = ₹500. Tracking daily spending is the first step to saving money.', 10, 0, TRUE, NULL, NULL),
('ST_MM_003', 'money_math', 'mental_math', 0, 'A meal costs ₹150. You eat out 4 times this week. Total food cost = ?', '600', NULL, '₹150 × 4 = ₹600. Small recurring expenses add up fast over a month.', 10, 0, TRUE, NULL, NULL),
('ST_MM_004', 'money_math', 'mental_math', 0, 'You earn ₹10,000 this month and spend ₹7,000. How much did you save?', '3000', NULL, '₹10,000 − ₹7,000 = ₹3,000. Savings = Income − Expenses. That''s personal finance in one formula.', 10, 0, TRUE, NULL, NULL),
('ST_MM_005', 'money_math', 'mental_math', 0, 'You split a ₹1,000 dinner bill equally among 4 friends. Your share = ?', '250', NULL, '₹1,000 ÷ 4 = ₹250. Splitting bills fairly keeps friendships intact and wallets honest.', 10, 0, TRUE, NULL, NULL),
('ST_MM_006', 'money_math', 'mental_math', 0, 'A ₹500 book is on sale for ₹50 off. Sale price = ?', '450', NULL, '₹500 − ₹50 = ₹450. Discounts reduce the marked price by a fixed amount.', 10, 0, TRUE, NULL, NULL),
('ST_MM_007', 'money_math', 'mental_math', 0, 'You save ₹500 every week. After 4 weeks, total savings = ?', '2000', NULL, '₹500 × 4 = ₹2,000. Even small regular savings build a meaningful cushion over time.', 10, 0, TRUE, NULL, NULL),
('ST_MM_008', 'money_math', 'mental_math', 0, 'Groceries cost ₹2,000 per month. Annual grocery bill = ?', '24000', NULL, '₹2,000 × 12 = ₹24,000. Multiplying monthly costs by 12 reveals your true yearly spending.', 10, 0, TRUE, NULL, NULL),
('ST_MM_009', 'money_math', 'mental_math', 0, 'You have ₹5,000 and spend ₹1,500. Remaining = ?', '3500', NULL, '₹5,000 − ₹1,500 = ₹3,500. Knowing your balance after spending is basic financial awareness.', 10, 0, TRUE, NULL, NULL),
('ST_MM_010', 'money_math', 'mental_math', 0, 'You buy 5 notebooks at ₹20 each. Total cost = ?', '100', NULL, '₹20 × 5 = ₹100. Unit price × quantity = total cost. Works for everything from groceries to stocks.', 10, 0, TRUE, NULL, NULL),
('ST_MM_011', 'money_math', 'mental_math', 0, '10% of ₹1,000 = ?', '100', NULL, '10% of any number = move the decimal one place left. ₹1,000 → ₹100. Simple rule to remember always.', 10, 0, TRUE, NULL, NULL),
('ST_MM_012', 'money_math', 'mental_math', 0, '50% of ₹500 = ?', '250', NULL, '50% means half. ₹500 ÷ 2 = ₹250. Half-price sales work on this exact math.', 10, 0, TRUE, NULL, NULL),
('ST_MM_013', 'money_math', 'mental_math', 0, '10% of ₹5,000 = ?', '500', NULL, '10% of ₹5,000 = ₹500. If your FD earns 10% annually on ₹5,000, you get ₹500 extra.', 10, 0, TRUE, NULL, NULL),
('ST_MM_014', 'money_math', 'mental_math', 0, '10% of ₹10,000 = ?', '1000', NULL, '10% = ₹1,000. A 10% salary hike on ₹10,000 means ₹1,000 more per month.', 10, 0, TRUE, NULL, NULL),
('ST_MM_015', 'money_math', 'mental_math', 0, '50% of ₹10,000 = ?', '5000', NULL, '₹10,000 ÷ 2 = ₹5,000. Investing 50% of your income is an ambitious but powerful savings goal.', 10, 0, TRUE, NULL, NULL),
('ST_MM_016', 'money_math', 'mental_math', 0, '25% of ₹1,000 = ?', '250', NULL, '25% = one quarter. ₹1,000 ÷ 4 = ₹250. Knowing fractions of your money helps you budget better.', 10, 0, TRUE, NULL, NULL),
('ST_MM_017', 'money_math', 'mental_math', 0, '20% of ₹500 = ?', '100', NULL, '20% = one-fifth. ₹500 ÷ 5 = ₹100. A 20% discount on ₹500 saves you ₹100.', 10, 0, TRUE, NULL, NULL),
('ST_MM_018', 'money_math', 'mental_math', 0, '10% of ₹50,000 = ?', '5000', NULL, '10% of ₹50,000 = ₹5,000. A 10% down payment on ₹50,000 item = ₹5,000 upfront.', 10, 0, TRUE, NULL, NULL),
('ST_MM_019', 'money_math', 'mental_math', 0, '50% of ₹1,00,000 = ?', '50000', NULL, 'Half of ₹1 lakh = ₹50,000. Knowing fractions of large numbers helps with monthly planning.', 10, 0, TRUE, NULL, NULL),
('ST_MM_020', 'money_math', 'mental_math', 0, '25% of ₹10,000 = ?', '2500', NULL, '₹10,000 ÷ 4 = ₹2,500. If your ₹10,000 investment grows 25%, you earn ₹2,500 extra.', 10, 0, TRUE, NULL, NULL),
('ST_MM_021', 'money_math', 'mental_math', 0, 'You buy a phone for ₹10,000 and sell for ₹12,000. Profit = ?', '2000', NULL, 'Profit = Selling price − Cost price. ₹12,000 − ₹10,000 = ₹2,000. You came out ahead!', 10, 0, TRUE, NULL, NULL),
('ST_MM_022', 'money_math', 'mental_math', 0, 'You lend a friend ₹1,000. They return ₹1,100. Extra you earned = ?', '100', NULL, '₹1,100 − ₹1,000 = ₹100. That ₹100 extra is essentially the ''return'' on your loan.', 10, 0, TRUE, NULL, NULL),
('ST_MM_023', 'money_math', 'mental_math', 0, 'You put ₹5,000 in a savings account. After a year, you have ₹5,500. Interest earned = ?', '500', NULL, '₹5,500 − ₹5,000 = ₹500 earned as interest. That''s a 10% annual return on your savings.', 10, 0, TRUE, NULL, NULL),
('ST_MM_024', 'money_math', 'mental_math', 0, 'You invest ₹1,000. It grows to ₹1,500. How much did it grow by?', '500', NULL, '₹1,500 − ₹1,000 = ₹500 growth. Tracking growth in rupee terms is the simplest return measure.', 10, 0, TRUE, NULL, NULL),
('ST_MM_025', 'money_math', 'mental_math', 0, 'You buy a TV for ₹20,000 and sell for ₹15,000. Loss = ?', '5000', NULL, '₹20,000 − ₹15,000 = ₹5,000 loss. Most electronics lose value over time.', 10, 0, TRUE, NULL, NULL),
('ST_MM_026', 'money_math', 'mental_math', 0, 'Save ₹1,000/month for 5 months. Total savings = ?', '5000', NULL, '₹1,000 × 5 = ₹5,000. Consistent saving, even small amounts, creates a meaningful fund.', 10, 0, TRUE, NULL, NULL),
('ST_MM_027', 'money_math', 'mental_math', 0, 'Your salary is ₹20,000. You save ₹5,000 this month. How much did you spend?', '15000', NULL, '₹20,000 − ₹5,000 = ₹15,000 spent. Savings first, spending second is the ideal order.', 10, 0, TRUE, NULL, NULL),
('ST_MM_028', 'money_math', 'mental_math', 0, 'You get a ₹2,000 bonus. You spend ₹500 and save the rest. Saved = ?', '1500', NULL, '₹2,000 − ₹500 = ₹1,500 saved. Saving most of unexpected income is a great wealth habit.', 10, 0, TRUE, NULL, NULL),
('ST_MM_029', 'money_math', 'mental_math', 0, 'A jacket costs ₹2,000. It''s discounted by ₹500. New price = ?', '1500', NULL, '₹2,000 − ₹500 = ₹1,500. Fixed amount discounts directly reduce the price by that amount.', 10, 0, TRUE, NULL, NULL),
('ST_MM_030', 'money_math', 'mental_math', 0, 'You earn ₹500 interest on ₹5,000 kept in bank for 1 year. How much total do you have?', '5500', NULL, '₹5,000 + ₹500 = ₹5,500. Total = Original amount + Interest. Your money grew by doing nothing.', 10, 0, TRUE, NULL, NULL),
('ST_MM_031', 'money_math', 'mental_math', 0, 'You pay ₹1,000/month for a loan. After 6 months, total paid = ?', '6000', NULL, '₹1,000 × 6 = ₹6,000. Monthly payment × number of months = total amount paid.', 10, 0, TRUE, NULL, NULL),
('ST_MM_032', 'money_math', 'mental_math', 0, 'Monthly expenses: Rent ₹5,000, Food ₹2,000, Travel ₹1,000. Total = ?', '8000', NULL, '₹5,000 + ₹2,000 + ₹1,000 = ₹8,000. A basic monthly budget tracks all your expense categories.', 10, 0, TRUE, NULL, NULL),
('ST_MM_033', 'money_math', 'mental_math', 0, 'You earn ₹15,000 and your rent is ₹5,000. Rent takes what fraction of income?', '.33', NULL, '₹5,000 ÷ ₹15,000 ≈ 1/3 of income on rent. Keeping rent below 30% is a common goal.', 10, 0, TRUE, NULL, NULL),
('ST_MM_034', 'money_math', 'mental_math', 0, 'Monthly payment is ₹2,000 for 12 months. Total repaid = ?', '24000', NULL, '₹2,000 × 12 = ₹24,000 total repaid. If you borrowed ₹20,000, you paid ₹4,000 extra as interest.', 10, 0, TRUE, NULL, NULL),
('ST_MM_035', 'money_math', 'mental_math', 0, 'Your phone bill is ₹500/month. Annual phone bill = ?', '6000', NULL, '₹500 × 12 = ₹6,000/year. Seeing annual costs makes you evaluate subscriptions more carefully.', 10, 0, TRUE, NULL, NULL),
('ST_MM_036', 'money_math', 'mental_math', 0, 'You have ₹10,000 budget. Spent ₹3,000 in week 1. Remaining for rest of the month = ?', '7000', NULL, '₹10,000 − ₹3,000 = ₹7,000 left. Tracking spend against a budget prevents month-end surprises.', 10, 0, TRUE, NULL, NULL),
('ST_MM_037', 'money_math', 'mental_math', 0, 'Subscription costs ₹150/month. Yearly cost = ?', '1800', NULL, '₹150 × 12 = ₹1,800/year. Small monthly charges can add up to significant annual costs.', 10, 0, TRUE, NULL, NULL),
('ST_MM_038', 'money_math', 'mental_math', 0, 'You borrow ₹5,000 from a friend and pay back ₹500/month. Paid back in how many months?', '10', NULL, '₹5,000 ÷ ₹500 = 10 months. Dividing total debt by monthly payment tells you your payoff timeline.', 10, 0, TRUE, NULL, NULL),
('ST_MM_039', 'money_math', 'mental_math', 0, 'Salary ₹25,000. Tax deducted ₹2,500. In-hand salary = ?', '22500', NULL, '₹25,000 − ₹2,500 = ₹22,500 take-home. Your total pay and in-hand salary differ because of deductions.', 10, 0, TRUE, NULL, NULL),
('ST_MM_040', 'money_math', 'mental_math', 0, 'You spend ₹100/day on average. Monthly spend (30 days) = ?', '3000', NULL, '₹100 × 30 = ₹3,000/month. Daily spending habits define your monthly financial picture.', 10, 0, TRUE, NULL, NULL),
('ST_TF_001', 'true_false', 'personal_finance', 0, 'Saving money every month helps you build financial security.', 'TRUE', NULL, 'Consistent saving creates a buffer for emergencies and a foundation for future plans.', 7, 0, TRUE, NULL, NULL),
('ST_TF_002', 'true_false', 'personal_finance', 0, 'You need a lot of money to start saving.', 'FALSE', NULL, 'You can start saving with as little as ₹100/month. The habit matters far more than the amount.', 7, 0, TRUE, NULL, NULL),
('ST_TF_003', 'true_false', 'banking', 0, 'A bank account is safer than keeping large amounts of cash at home.', 'TRUE', NULL, 'Bank deposits are protected. Large cash at home risks theft, fire, or loss.', 7, 0, TRUE, NULL, NULL),
('ST_TF_004', 'true_false', 'banking', 0, 'Keeping cash under your mattress is a safe way to store money.', 'FALSE', NULL, 'Cash at home has no protection and loses value over time as prices of things rise.', 7, 0, TRUE, NULL, NULL),
('ST_TF_005', 'true_false', 'personal_finance', 0, 'Spending less than you earn is the first rule of personal finance.', 'TRUE', NULL, 'Living below your means is the foundation every financial expert agrees on.', 7, 0, TRUE, NULL, NULL),
('ST_TF_006', 'true_false', 'banking', 0, 'A credit card is the same as free money.', 'FALSE', NULL, 'A credit card is a short-term loan. Unpaid balances attract high interest charges.', 7, 0, TRUE, NULL, NULL),
('ST_TF_007', 'true_false', 'banking', 0, 'If you borrow money, you usually have to pay back more than you borrowed.', 'TRUE', NULL, 'The extra amount you pay back is called interest — the cost of borrowing money.', 7, 0, TRUE, NULL, NULL),
('ST_TF_008', 'true_false', 'personal_finance', 0, 'Rich people don''t need to budget.', 'FALSE', NULL, 'Many wealthy people budget carefully. Budgeting is how you stay wealthy, not just how you get there.', 7, 0, TRUE, NULL, NULL),
('ST_TF_009', 'true_false', 'banking', 0, 'A credit card lets you buy now and pay later.', 'TRUE', NULL, 'Credit cards provide short-term loans. Pay in full by due date to avoid extra charges.', 7, 0, TRUE, NULL, NULL),
('ST_TF_010', 'true_false', 'personal_finance', 0, 'If a product is expensive, it''s always better quality.', 'FALSE', NULL, 'Price doesn''t always equal quality. Comparing reviews is smarter than assuming price = quality.', 7, 0, TRUE, NULL, NULL),
('ST_TF_011', 'true_false', 'personal_finance', 0, 'Prices of most things tend to rise over time.', 'TRUE', NULL, 'This is called inflation. ₹100 today buys less than ₹100 did 10 years ago.', 7, 0, TRUE, NULL, NULL),
('ST_TF_012', 'true_false', 'banking', 0, 'Taking a loan is always a bad financial decision.', 'FALSE', NULL, 'Loans for productive things like education or property can be smart when managed well.', 7, 0, TRUE, NULL, NULL),
('ST_TF_013', 'true_false', 'investing', 0, 'Investing money can help it grow faster than keeping it idle.', 'TRUE', NULL, 'Idle money loses value. Investments in FDs or funds can grow faster than inflation.', 7, 0, TRUE, NULL, NULL),
('ST_TF_014', 'true_false', 'personal_finance', 0, 'You only need to think about retirement when you''re 50.', 'FALSE', NULL, 'Starting retirement savings at 25 vs 50 can mean much more money saved due to time.', 7, 0, TRUE, NULL, NULL),
('ST_TF_015', 'true_false', 'banking', 0, 'You should read what you''re signing before taking a loan.', 'TRUE', NULL, 'Loan agreements contain terms about interest and repayment — always read them.', 7, 0, TRUE, NULL, NULL),
('ST_TF_016', 'true_false', 'investing', 0, 'All investments are equally risky.', 'FALSE', NULL, 'FDS carry very low risk. Stocks carry high risk. Risk varies greatly.', 7, 0, TRUE, NULL, NULL),
('ST_TF_017', 'true_false', 'personal_finance', 0, 'Having an emergency fund helps you avoid taking loans during a crisis.', 'TRUE', NULL, 'Experts recommend 3-6 months of expenses saved as an emergency fund.', 7, 0, TRUE, NULL, NULL),
('ST_TF_018', 'true_false', 'banking', 0, 'Banks give you free money when you open an account.', 'FALSE', NULL, 'Banks pay interest on deposits, but only on the amount saved and only while it''s with them.', 7, 0, TRUE, NULL, NULL),
('ST_TF_019', 'true_false', 'investing', 0, 'Starting to save at a young age gives your money more time to grow.', 'TRUE', NULL, 'The earlier you start, the longer your money has to grow. Time is a powerful tool.', 7, 0, TRUE, NULL, NULL),
('ST_TF_020', 'true_false', 'personal_finance', 0, 'Winning a lot of money means you''ll never have financial problems.', 'FALSE', NULL, 'Many lottery winners go broke. Managing money is a skill, not just about the amount.', 7, 0, TRUE, NULL, NULL),
('ST_TF_021', 'true_false', 'insurance', 0, 'Insurance helps you cover large unexpected expenses.', 'TRUE', NULL, 'Health or life insurance protects you from major events for a small regular cost.', 7, 0, TRUE, NULL, NULL),
('ST_TF_022', 'true_false', 'insurance', 0, 'Insurance is a waste of money if you don''t make a claim.', 'FALSE', NULL, 'Insurance is buying peace of mind. Not claiming means you were lucky, not that you wasted money.', 7, 0, TRUE, NULL, NULL),
('ST_TF_023', 'true_false', 'personal_finance', 0, 'Your income minus your expenses equals your savings.', 'TRUE', NULL, 'Savings = Income − Expenses. This is the starting point of financial planning.', 7, 0, TRUE, NULL, NULL),
('ST_TF_024', 'true_false', 'investing', 0, 'The stock market always goes up in the short term.', 'FALSE', NULL, 'Stocks can fall significantly in the short term. Long-term trends are usually upward.', 7, 0, TRUE, NULL, NULL),
('ST_TF_025', 'true_false', 'personal_finance', 0, 'A shop selling something for 50% off means you pay half the price.', 'TRUE', NULL, '50% off = half price. A ₹1,000 item at 50% off costs ₹500.', 7, 0, TRUE, NULL, NULL),
('ST_TF_026', 'true_false', 'investing', 0, 'You should always withdraw all your money if the stock market falls.', 'FALSE', NULL, 'Selling during a fall locks in your losses. Markets historically recover over time.', 7, 0, TRUE, NULL, NULL),
('ST_TF_027', 'true_false', 'banking', 0, 'Paying your bills on time helps maintain a good credit history.', 'TRUE', NULL, 'Timely payments are the biggest factor in building a strong credit reputation.', 7, 0, TRUE, NULL, NULL),
('ST_TF_028', 'true_false', 'personal_finance', 0, 'Splitting a restaurant bill equally is the only fair way.', 'FALSE', NULL, 'Fairness depends on what each person ordered. Equal splitting is just one option.', 7, 0, TRUE, NULL, NULL),
('ST_TF_029', 'true_false', 'personal_finance', 0, 'Comparing prices before buying can save you money.', 'TRUE', NULL, 'Price comparison is one of the easiest ways to spend less for the same thing.', 7, 0, TRUE, NULL, NULL),
('ST_TF_030', 'true_false', 'personal_finance', 0, 'A zero bank balance is fine as long as you have a credit card.', 'FALSE', NULL, 'Over-relying on credit leads to debt. A savings buffer is always safer.', 7, 0, TRUE, NULL, NULL),
('ST_TF_031', 'true_false', 'investing', 0, 'Compound interest means you earn interest on your interest.', 'TRUE', NULL, 'Your interest gets added to your total, and next time you earn interest on the larger amount.', 7, 0, TRUE, NULL, NULL),
('ST_TF_032', 'true_false', 'banking', 0, 'If something is on monthly payments (EMI), it''s affordable for you.', 'FALSE', NULL, 'Payments can stretch beyond your means. Always check if total payments fit your budget.', 7, 0, TRUE, NULL, NULL),
('ST_TF_033', 'true_false', 'personal_finance', 0, 'Spending money on things that lose value (like electronics) is higher risk than on things that grow (like property).', 'TRUE', NULL, 'Electronics lose value quickly. Borrowing to buy them means you owe money on something worth less each day.', 7, 0, TRUE, NULL, NULL),
('ST_TF_034', 'true_false', 'banking', 0, 'Bigger loans always have smaller interest rates.', 'FALSE', NULL, 'Interest rates depend on the type of loan and your credit score, not necessarily the loan size.', 7, 0, TRUE, NULL, NULL),
('ST_TF_035', 'true_false', 'investing', 0, 'Diversifying means not putting all your money in one place.', 'TRUE', NULL, 'Spreading money across different assets reduces the risk of losing everything if one fails.', 7, 0, TRUE, NULL, NULL),
('ST_TF_036', 'true_false', 'investing', 0, 'You can trust any investment that promises guaranteed high returns.', 'FALSE', NULL, 'Guaranteed high returns are a major warning sign. High returns always come with higher risk.', 7, 0, TRUE, NULL, NULL),
('ST_TF_037', 'true_false', 'personal_finance', 0, 'A higher salary doesn''t automatically mean more savings.', 'TRUE', NULL, 'Expenses often rise with income. Savings depend on your habits, not just your income size.', 7, 0, TRUE, NULL, NULL),
('ST_TF_038', 'true_false', 'personal_finance', 0, 'Your salary is the only measure of your financial health.', 'FALSE', NULL, 'Net worth, savings rate, and debt level matter more than salary for true financial health.', 7, 0, TRUE, NULL, NULL),
('ST_TF_039', 'true_false', 'personal_finance', 0, 'Tracking your spending helps you understand where your money goes.', 'TRUE', NULL, 'Most people underestimate certain costs until they write them down. Awareness is the first step.', 7, 0, TRUE, NULL, NULL),
('ST_TF_040', 'true_false', 'personal_finance', 0, 'It''s impossible to save money when your salary is low.', 'FALSE', NULL, 'Saving even a small part of any income builds the habit. The amount grows as your income does.', 7, 0, TRUE, NULL, NULL),
('ST_MCQ_001', 'scenario_mcq', 'personal_finance', 0, 'You want to buy a phone worth ₹30,000 but only have ₹15,000. What''s the smartest first step?', 'Save more money until you can afford it', '["Save more money until you can afford it","Immediately take a credit card loan","Buy it anyway and worry later","Ask your boss for a ₹30,000 advance"]', 'Saving up avoids debt and interest costs. Patience is a financial superpower.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_002', 'scenario_mcq', 'personal_finance', 0, 'You get ₹10,000 as a Diwali bonus. What should you do FIRST?', 'Set aside some for savings or an emergency fund first', '["Spend it all on shopping — it''s a bonus!","Set aside some for savings or an emergency fund first","Lend it to a friend","Convert it all to cash and keep at home"]', 'Unexpected income is a great opportunity to build savings. Spend what''s left, not the other way round.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_003', 'scenario_mcq', 'personal_finance', 0, 'Two shops sell the same bag — Shop A for ₹800, Shop B for ₹650. What''s the smart choice?', 'Buy from Shop B — same product, lower price', '["Buy from Shop A — it must be better quality","Buy from Shop B — same product, lower price","Don''t buy at all","Flip a coin"]', 'If the product is identical, always pay less. Comparing prices is basic smart spending.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_004', 'scenario_mcq', 'personal_finance', 0, 'Your monthly expenses are ₹15,000 but your salary is ₹12,000. What''s the problem?', 'You are spending more than you earn — this creates debt', '["Your salary is too high","You are spending more than you earn — this creates debt","Your expenses are perfectly fine","You need to work fewer hours"]', 'Spending more than you earn means borrowing or using up savings — unsustainable over time.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_005', 'scenario_mcq', 'investing', 0, 'A friend offers you high returns guaranteed in just 1 month. You should:', 'Be very suspicious — guaranteed high returns are a major warning sign', '["Invest everything immediately — this is a great deal","Be very suspicious — guaranteed high returns are a major warning sign","Tell all your family to invest too","Ask for even higher returns"]', 'Guaranteed high returns in a short time are almost always scams. If it sounds too good to be true, it is.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_006', 'scenario_mcq', 'personal_finance', 0, 'You have ₹50,000 saved. An essential repair costs ₹20,000. You should:', 'Use your savings — this is what an emergency fund is for', '["Take a personal loan immediately","Use your savings — this is what an emergency fund is for","Ignore the repair","Ask your employer for a salary advance"]', 'Emergency funds exist for exactly this — unexpected essential costs. Using savings beats paying loan interest.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_007', 'scenario_mcq', 'banking', 0, 'Which is the best way to avoid forgetting to pay your credit card bill?', 'Set up an automatic payment or calendar reminder', '["Hope you remember each month","Set up an automatic payment or calendar reminder","Stop using the credit card entirely","Pay only when the bank calls you"]', 'Automation removes the chance of human error. One missed payment can hurt your credit reputation.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_008', 'scenario_mcq', 'personal_finance', 0, 'Your monthly income is ₹20,000. How much should you ideally save at minimum?', 'At least ₹2,000 (10%) — start small and build the habit', '["₹0 — spend everything","At least ₹2,000 (10%) — start small and build the habit","₹19,000 — save almost everything","Whatever is left after fun spending"]', '10% is a classic starting savings rule. The habit matters more than the amount at this stage.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_009', 'scenario_mcq', 'personal_finance', 0, 'You want to buy something costing ₹5,000 but have only ₹3,000. Which option costs the least total?', 'Save the remaining ₹2,000 over 2 months and pay cash', '["Buy on credit card with high interest","Take a personal loan","Save the remaining ₹2,000 over 2 months and pay cash","Ask for a discount"]', 'Paying cash after saving means zero interest. Every loan option costs more than the original price.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_010', 'scenario_mcq', 'personal_finance', 0, 'Which habit best helps you know if you''re overspending?', 'Tracking your spending every week', '["Checking your bank balance once a year","Tracking your spending every week","Spending until your account is empty","Asking a friend to guess"]', 'Weekly tracking gives you real-time awareness and allows small corrections before problems grow large.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_011', 'scenario_mcq', 'banking', 0, 'Choosing between keeping large cash at home or putting it in a bank Fixed Deposit (FD):', 'Bank FD is better — it''s safer and earns interest', '["Cash at home is better — you can see it","Bank FD is better — it''s safer and earns interest","Both are exactly the same","Keep half in cash and hide the other half"]', 'Bank deposits are protected and help your money grow. Cash at home risks loss and earns nothing.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_012', 'scenario_mcq', 'personal_finance', 0, 'When deciding whether to buy a house or rent one, what''s a key factor?', 'Compare your monthly budget and how long you plan to stay', '["Buying is always better for everyone","Renting is always a waste of money","Compare your monthly budget and how long you plan to stay","Ask your neighbor what they did"]', 'The right choice depends on your specific financial situation and future plans, not a single rule.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_013', 'scenario_mcq', 'personal_finance', 0, 'A friend is pressuring you to join a ''wealth club'' that requires ₹5,000 and recruiting others. You should:', 'Be careful — this sounds like a pyramid scheme which is risky and often illegal', '["Join immediately — friends know best","Be careful — this sounds like a pyramid scheme which is risky and often illegal","Invest ₹10,000 instead","Wait until next week"]', 'If an ''investment'' relies on recruiting people rather than selling a product, it''s a major warning sign.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_014', 'scenario_mcq', 'personal_finance', 0, 'On a salary slip, what is the difference between ''Gross Pay'' and ''Net Pay''?', 'Net is what you take home, Gross is total before taxes', '["Gross is what you take home, Net is total before taxes","Net is what you take home, Gross is total before taxes","They are exactly the same","Gross is for managers, Net is for others"]', 'Net pay (take-home) is Gross pay minus deductions like taxes or insurance.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_015', 'scenario_mcq', 'banking', 0, 'When comparing two loans, which one is generally better for you?', 'The one with the lower total cost of repayment', '["The one with the higher interest rate","The one with the lower total cost of repayment","The one from a bigger bank","The one with the longest name"]', 'Always look at the total amount you''ll pay back, including all interest and fees.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_016', 'scenario_mcq', 'banking', 0, 'What does it mean when a bank says your FD will earn 6% per year?', 'You earn 6 extra rupees for every ₹100 you deposit, every year', '["You get 6 rupees back total","You earn 6 extra rupees for every ₹100 you deposit, every year","You must pay the bank ₹6 every year","You can only withdraw 6% of your money"]', '6% per year means ₹6 earned per ₹100 deposited annually. On ₹10,000, that''s ₹600 extra.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_017', 'scenario_mcq', 'banking', 0, 'What is a ''loan''?', 'Money you borrow and must repay with extra charges', '["Free money given by the government","Money you borrow and must repay with extra charges","A type of investment that grows","Money your employer gives as a gift"]', 'A loan is borrowed money. You repay the original amount plus interest (the borrowing cost).', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_018', 'scenario_mcq', 'banking', 0, 'Why do banks pay you interest on savings?', 'Because they use your money to give loans to others and share some profit with you', '["Because they feel generous","Because they use your money to give loans to others and share some profit with you","It''s a government rule for customers only","Banks don''t actually pay interest"]', 'Banks take deposits, lend at higher rates, and share some of that as interest to you.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_019', 'scenario_mcq', 'personal_finance', 0, 'What does ''spending within your means'' mean?', 'Spending only what you can afford without going into debt', '["Only buying expensive things","Spending only what you can afford without going into debt","Always buying the cheapest option","Never spending money on fun"]', 'Living within your means = not spending more than you earn. It''s a fundamental principle.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_020', 'scenario_mcq', 'insurance', 0, 'What is the main purpose of health insurance?', 'To cover large medical costs so they don''t hurt your finances', '["To save money on groceries","To cover large medical costs so they don''t hurt your finances","To make you healthier","To avoid going to doctors"]', 'Health insurance protects you from paying massive hospital bills out of pocket in an emergency.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_021', 'scenario_mcq', 'investing', 0, 'If you invest ₹1,000 and earn 10% in a year, how much total do you have?', '₹1,100', '["₹10,000","₹1,100","₹1,010","₹900"]', '10% of ₹1,000 = ₹100. Total = ₹1,000 + ₹100 = ₹1,100. Your money grew while you waited.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_022', 'scenario_mcq', 'personal_finance', 0, 'What happens to your money''s value when prices of things rise (inflation)?', 'You can buy less with the same money', '["You can buy more with the same money","You can buy less with the same money","Nothing changes","Your salary automatically increases"]', 'Inflation means ₹100 buys fewer things over time. This is why investing is important.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_023', 'scenario_mcq', 'personal_finance', 0, 'What is a ''budget''?', 'A plan for how much you will earn and spend', '["A list of everything you own","A plan for how much you will earn and spend","A type of bank account","A loan for starting a business"]', 'A budget helps you track where your money goes and ensures you save for your future.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_024', 'scenario_mcq', 'personal_finance', 0, 'What is ''Net Worth''?', 'Everything you own minus everything you owe', '["Total amount you earn in a year","Everything you own minus everything you owe","The balance in your main bank account","The cost of your most expensive possession"]', 'Net worth is the true measure of your wealth. It''s assets minus liabilities.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_025', 'scenario_mcq', 'banking', 0, 'What is a ''credit score''?', 'A number that shows how reliable you are at paying back loans', '["Points you get for shopping","A number that shows how reliable you are at paying back loans","The amount of money you have in the bank","Your ranking compared to other customers"]', 'A good credit score helps you get loans easier and at better interest rates.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_026', 'scenario_mcq', 'investing', 0, 'Why should you diversify your investments?', 'To reduce the risk of losing all your money if one thing fails', '["To make things more complicated","To reduce the risk of losing all your money if one thing fails","To pay more fees to the bank","Because the government requires it"]', 'Diversifying — or spreading your money — protects you from losing everything in a single crash.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_027', 'scenario_mcq', 'investing', 0, 'In simple terms, what is a ''Mutual Fund''?', 'A pool of money from many people used to buy many different investments together', '["A loan you give to a friend","A pool of money from many people used to buy many different investments together","A private bank account","A type of credit card"]', 'Mutual funds allow you to own a slice of many investments even with a small amount of money.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_028', 'scenario_mcq', 'investing', 0, 'Why is it better to start investing when you''re young?', 'Because your money has a lot more time to grow through compounding', '["Because young people have more luck","Because your money has a lot more time to grow through compounding","Because banks only like young customers","It''s not actually better"]', 'Starting early is the biggest advantage you can have. Even small amounts grow large over decades.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_029', 'scenario_mcq', 'taxation', 0, 'In simple terms, what is ''income tax''?', 'A part of your earnings that goes to the government to pay for public services', '["A fee you pay to use a bank","A part of your earnings that goes to the government to pay for public services","A bonus your boss gives you","The cost of opening a business"]', 'The government uses tax money for things like roads, schools, and hospitals.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_030', 'scenario_mcq', 'banking', 0, 'What is an ''EMI''?', 'A fixed amount you pay every month to repay a loan', '["A type of fast payment","A fixed amount you pay every month to repay a loan","An email notification from your bank","A special savings account"]', 'EMI stands for Equated Monthly Installment. It helps you pay back a large loan in small, equal parts.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_031', 'scenario_mcq', 'personal_finance', 0, 'Your first salary just arrived. Friends want you to celebrate with an expensive dinner. You should:', 'Celebrate reasonably within a budget, then save the rest', '["Spend a huge amount — you earned it!","Celebrate reasonably within a budget, then save the rest","Not celebrate at all","Pay for everyone using a credit card loan"]', 'Celebrating is fine! The key is deciding a budget before you spend, not after.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_032', 'scenario_mcq', 'banking', 0, 'You just got your first credit card. What is the safest habit?', 'Use it for planned things and pay the full bill before the due date', '["Max out the limit immediately","Use it for planned things and pay the full bill before the due date","Never use it at all","Pay only the minimum amount due each month"]', 'Full payment means no extra interest and a strong credit reputation.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_033', 'scenario_mcq', 'personal_finance', 0, 'Your essential costs take ₹18,000 of your ₹22,000 salary. What should you do?', 'Look for ways to spend less or earn more — ₹4,000 extra is very tight', '["Ignore it — ₹18,000 is small","Look for ways to spend less or earn more — ₹4,000 extra is very tight","Take a loan to feel more comfortable","Immediately quit your job"]', 'Only ₹4,000 left after essentials leaves almost no room for savings or emergencies.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_034', 'scenario_mcq', 'personal_finance', 0, 'You get a small raise at work. The smartest move is:', 'Keep your expenses the same and save the extra amount', '["Immediately move to a more expensive house","Keep your expenses the same and save the extra amount","Spend it all on a luxury watch","Quit and travel until the money is gone"]', 'Saving your salary increases is the fastest way to build real wealth.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_035', 'scenario_mcq', 'personal_finance', 0, 'Which of these is a ''Good Debt''?', 'Education loan to improve your career', '["Credit card loan for a vacation","Education loan to improve your career","Payday loan for a new phone","None — all debt is bad"]', 'Good debt is used for things that grow in value or increase your earning power.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_036', 'scenario_mcq', 'personal_finance', 0, 'What is an ''Asset''?', 'Something you own that has value or earns you money', '["Something you own that has value or earns you money","Something you owe to others","A monthly expense like rent","The total tax you pay"]', 'Assets include savings, property, and investments. They help build your wealth.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_037', 'scenario_mcq', 'personal_finance', 0, 'What is a ''Liability''?', 'A debt or obligation you owe to others', '["Something you own that is valuable","A debt or obligation you owe to others","Your total income for a year","A type of insurance"]', 'Liabilities include loans and credit card balances. They reduce your total wealth.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_038', 'scenario_mcq', 'personal_finance', 0, 'Which habit helps you prepare for the unexpected?', 'Building an emergency fund of 3-6 months'' expenses', '["Spending everything as soon as you get it","Building an emergency fund of 3-6 months'' expenses","Hope for the best and don''t worry","Lending all your extra money to friends"]', 'An emergency fund is your financial safety net for things like medical bills or job loss.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_039', 'scenario_mcq', 'personal_finance', 0, 'Why should you track your spending?', 'To see where your money is going and find ways to save', '["To see where your money is going and find ways to save","Because it''s a fun hobby","Because the bank charges you if you don''t","To show off to your friends"]', 'Tracking is the only way to know if your spending matches your goals.', 12, 0, TRUE, NULL, NULL),
('ST_MCQ_040', 'scenario_mcq', 'investing', 0, 'What is the simplest way to explain ''Risk'' in investing?', 'The chance that you might lose some or all of your money', '["The chance that you might lose some or all of your money","The total profit you earn","The cost of opening a demat account","The time it takes to get your money back"]', 'Higher risk usually means higher potential returns, but also more chance of losing money.', 12, 0, TRUE, NULL, NULL),
